package main.data.factory;

import java.util.ArrayList;
import java.util.List;

import main.data.entities.Arena;

public class SimpleArenaFactory extends ArenaFactory
{
	private static List<Arena> arenas = new ArrayList<Arena>();
	private static SimpleArenaFactory instance = null;

	protected SimpleArenaFactory()
	{
		if (arenas.size() == 0)
			defineArenas();
	}

	public static SimpleArenaFactory getInstance()
	{
		if (instance == null)
			instance = new SimpleArenaFactory();

		return instance;
	}
	
	@Override
	public Arena generateArena(int arenaNumber)
	{
		if (arenaNumber < 0 || arenaNumber > 19)
			throw new IndexOutOfBoundsException("Invalid arena number specified: " + arenaNumber);
		
		return arenas.get(arenaNumber).clone();
	}
	
	private void defineArenas()
	{
		try
		{
			arenas.add(new Arena(0, "BRIDGES", ""));
			arenas.add(new Arena(1, "JACKAL'S LAIR", ""));
			arenas.add(new Arena(2, "CRISSICK", ""));
			arenas.add(new Arena(3, "WHIRLWIND", ""));
			
			arenas.add(new Arena(4, "THE VOID", ""));
			arenas.add(new Arena(5, "OBSERVATORY", ""));
			arenas.add(new Arena(6, "THE ABYSS", ""));
			arenas.add(new Arena(7, "GADEL SPYRE", ""));//gadel spyre
			
			arenas.add(new Arena(8, "FULCRUM", ""));
			arenas.add(new Arena(9, "SAVANNA", ""));//savanna
			arenas.add(new Arena(10, "BARROW", ""));
			arenas.add(new Arena(11, "MAELSTROM", ""));
			
			arenas.add(new Arena(12, "VAULT", ""));
			arenas.add(new Arena(13, "NEXUS", ""));
			arenas.add(new Arena(14, "DARKSUN", ""));
			arenas.add(new Arena(15, "BADLANDS", ""));
			
			arenas.add(new Arena(16, "LIGHTWAY", ""));
			arenas.add(new Arena(17, "EYES", ""));
			arenas.add(new Arena(18, "DARKSTAR", ""));
			arenas.add(new Arena(19, "SPACECOM", ""));
		}
		catch(IllegalArgumentException e)
		{
			System.out.print("ERROR: Arena definition failed! Check initialization strings.");
		}
	}
}
